mapper.select:
select * from bi_dashboard_share

        `type` = 'USER'

        and dashboard_id = #{dashboardId}


        and (username like concat('%',#{keyword},'%')

            or username in

                #{usr}


        )


order by id desc

select distinct t.*,r.name as role_name from bi_dashboard_share t join bi_auth_role r on t.role_id = r.id join bi_auth_role_user usr on r.id = usr.role_id
`type` = 'ROLE'
and dashboard_id = #{dashboardId}
and (r.name like concat('%',#{keyword},'%') or r.description like concat('%',#{keyword},'%')
or usr.username in
#{u}
)
order by t.create_time desc
