mapper.select:
select t.id, t.workspace_id, t.name, t.description, t.report_type, t.dataset_id, t.creator, t.operator, t.create_time, t.update_time, t.query_param, if(f.id is null, false, true) as favorite, dash.dashboard_count, ds.name as datasetName from bi_report t left join bi_favorite f on t.id = f.target_id and f.position = 'REPORT' and f.username = #{username} left join ( select c.report_id as report_id,count(*) as dashboard_count from bi_dashboard_component c join bi_dashboard d on c.dashboard_id = d.id and d.version = c.version and d.deleted = 0 and c.type = 'REPORT' and d.status = 'ONLINE' group by c.report_id ) dash on dash.report_id = t.id join (select id,name from bi_dataset where status = 'ONLINE' and deleted =0) ds on t.dataset_id = ds.id
t.deleted = 0
and t.workspace_id = #{workspaceId}
and (t.name like concat('%',#{keyword},'%') or t.description like concat('%',#{keyword},'%')
or t.creator in (
#{usr}
)
)
and t.creator = #{creator}
and f.id is not null
and t.dataset_id in
#{did}
order by
t.`${sortField}` ${sortType}
t.create_time desc
